import API_ENDPOINT from"../../scripts/globals/api-endpoint";class RestaurantAppSource{static async restaurantList(){const t=await fetch(API_ENDPOINT.LIST);return(await t.json()).restaurants}static async detailMovie(t){const a=await fetch(API_ENDPOINT.DETAIL(t));return(await a.json()).restaurant}static async addReview(t){return(await fetch(API_ENDPOINT.ADD_REVIEW,{method:"POST",headers:{"Content-Type":"application/json","X-Auth-Token":12345},body:JSON.stringify(t)})).json()}}export default RestaurantAppSource;